shader:
#version 300 es
    precision mediump float;

    in vec2 vPixelCoord;
    out vec4 fColor;

    uniform sampler2D uStageTex;
    uniform float uTime;
    uniform vec2 uViewportSize;
    uniform vec2 uMousePos;

    void main() {
        fColor = texture(uStageTex,vPixelCoord)*vec4(uMousePos/uViewportSize,1,1);
    }
